mapper.select:
select   from t_customer where 1=1 

	    and id = #{obj.id}


	    and id = #{obj.id}


	 	and  ch_name like #{obj.chName}


	     and nick_name like #{obj.nickName}


	     and sex = #{obj.sex}


	     and sex != #{obj.webSex}


	     and openId = #{obj.openId}


	     and loca = #{obj.loca}


	     and email = #{obj.email}


	    and age  >= #{obj.ageFrom}


	    and age  <= #{obj.ageTo}


	    and height  >= #{obj.heightFrom}


	    and height  <= #{obj.heightTo}

 limit  #{page.start}, #{page.pageSize}

